import React,{useState} from 'react';
import './App.css';

function App() {

const [newItem, setNewItem] = useState("");
const [items, setItems] = useState([]);

    function addItem () {

      if (!newItem){
        alert('enter item')
        return;
      }

    const items ={
      id: Math.floor(Math.random() * 1000),
      value: newItem
    };

    setItems(oldList => [...oldList, items]);
    setNewItem("");


    console.log(items);




    }
    function deleteItem(id){
      const newArray = items.filter(items=>items.id !==id);
      setItems(newArray);
    }


    return (

    <div className='App'>
      <div className='todo'>
       <h1 >TO DO LIST</h1>
      <div>
        <input
        type='text'
        placeholder='add item'
        value={newItem}
        onChange = {e=> setNewItem(e.target.value)}

    /> 
       <button onClick={()=> addItem()}>add</button>
     </div>
      <ul>

        {
          items.map(items => {

            return(

              <li  className='x' key={items.id}>{items.value} <button  onClick={()=> deleteItem(items.id)}>‚ùå</button>  </li>,
              <input onClick={()=> deleteItem(items.id)} type="checkbox" />
              )
          })
        }
      </ul>

      </div>


    </div>
  );
}

export default App;
