    }
  return (

    <div className='App'>
       <h1>TO DO LIST</h1>
      <div>
        <input
        type='text'
        placeholder='add item'
        value={newItem}
        onChange = {e=> setNewItem(e.target.value)}

    />
       <button onClick={()=> addItem()}>add</button>
      </div>


    </div>
  );
}

export default App;
