    function addItem () {

    const items ={
      id: Math.floor(Math.random() * 1000),
      value: newItem
    };

    setItems(oldList => [...oldList, items]);
    setNewItem("");








    }
  return (

    <div className='App'>
       <h1>TO DO LIST</h1>
      <div>
        <input
        type='text'
        placeholder='add item'
        value={newItem}
        onChange = {e=> setNewItem(e.target.value)}

    />
