import './App.css';


function App() {
const [newItem, setNewItem] = useState("");
const [items, setItems] = useState([]);

    function addItem () {

      if (!newItem){
        alert('enter item')
        return;
      }

    const items ={
      id: Math.floor(Math.random() * 1000),
      value: newItem
    };

    setItems(oldList => [...oldList, items]);
    setNewItem("");


    console.log(items);
}

