    };
    return (

    <div className='App'>
      <div className='todo'>
       <h1 >TO DO LIST</h1>
      <div>
        <input
        type='text'
        placeholder='add item'
        value={newItem}
        onChange = {e=> setNewItem(e.target.value)}

    /> 
       <button onClick={()=> addItem()} >add</button>
     </div>
      <ul>

        {
          items.map(items => {

            return(

              <li  key={items.id}>{items.value} <button className='x'   onClick={()=> deleteItem(items.id)}>‚ùå</button><input type="checkbox"  /></li>

            )
          })
        }
      </ul>

      </div>


    </div>
  );
}

export default App;
