import React,{useState} from 'react';
import './App.css';

function App() {

const [newItem, setNewItem] = useState("yyy");
const [items, setItems] = useState([]);

    function addItem () {

      if (!newItem){
        alert('enter item')
        return;
      }

    const items ={
      id: Math.floor(Math.random() * 1000),
      value: newItem
    };

    setItems(oldList => [...oldList, items]);
    setNewItem("");


    console.log(items);




    }
    function deleteItem(id){
      const newArray = items.filter(items=>items.id !==id);
      setItems(newArray);
    }


    return (

    <div className='App'>
       <h1>TO DO LIST</h1>
      <div>
        <input
        type='text'
        placeholder='add item'
        value={newItem}
        onChange = {e=> setNewItem(e.target.value)}

    />
       <button onClick={()=> addItem()}>add</button>

      <ul>
        {
          items.map(items => {
            return(
              <li key={items.id}>{items.value} <button onClick={()=> deleteItem(items.id)}>delete.png</button></li>
            )
          })
        }
      </ul>

      </div>


    </div>
  );
}

export default App;
